"""
给定一个字符串 s 和一些长度相同的单词 words。在 s 中找出可以恰好串联 words 中所有单词的子串的起始位置。

注意子串要与 words 中的单词完全匹配，中间不能有其他字符，但不需要考虑 words 中单词串联的顺序。
"""


class Solution:
    def findSubstring1(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        if '' in words:
            return []
        if not s or not words:
            return []
        lss = len(words) * len(words[0])
        times = {}
        for w in words:
            if len(w) == len(words[0]):
                times[w] = times[w] + 1 if w in times else 1
            else:
                return []
        res = []

        return res

    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        while '' in words:
            words.remove('')
        if not s or not words:
            return []
        sd = {}
        for w in words:
            if w[0] in sd.keys():
                sd[w[0]].append(w)
            else:
                sd[w[0]] = [w]
        sub_str_len = sum([len(w) for w in words])
        used = []
        unused = words
        res = []
        for i in range(len(s) - sub_str_len + 1):
            end = i
            while end < i + sub_str_len:
                if s[end] in sd:
                    for w in sd[s[end]]:
                        if s[end: end + len(w)] == w and w in unused:
                                unused.remove(w)
                                used.append(w)
                                end += len(w)
                                break
                    else:
                        unused += used
                        used = []
                        break
                else:
                    unused += used
                    used = []
                    break
            else:
                res.append(i)
                unused += used
                used = []
        return res

    def findSubstring2(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        if '' in words:
            return []
        if not s or words == []:
            return []
        len_substr = len(words) * len(words[0])
        times = {}
        # 统计words中各词出现的频率
        for w in words:
            if len(w) == len(words[0]):
                times[w] = times[w] + 1 if w in times else 1
            else:
                return []
        res = []
        # 这个算法遍历的次数少，实际上是利用单词长度一样，只对s的几个（单词长度个）子串进行寻找。每次寻找都对整个串进行。
        # 如此，寻找过一个单词后，再次寻找则是重复的（0处的寻找一次步进与其一致）
        for i in range(min(len(words[0]), len(s) - len_substr + 1)):
            self.find_fix_len_substr(i, len(words[0]), len_substr, s, times, res)
        return res

    def find_fix_len_substr(self, str_start, len_word, len_substr, s, times, res):
        """
        从头寻找字串到尾，寻找所有可能的子串，前进的步长是单词长度。
        """
        word_start = str_start
        curr = {}
        while str_start + len_substr <= len(s):
            word = s[word_start:word_start + len_word]
            word_start += len_word
            if word not in times:
                str_start = word_start
                curr.clear()
            else:
                curr[word] = curr[word] + 1 if word in curr else 1
                # 如果有多的则把我们多的那部分丢弃
                while curr[word] > times[word]:
                    # 原子串头部那一节出现频率减一
                    curr[s[str_start:str_start + len_word]] -= 1
                    # 移动子串头部，原子串头部被丢弃
                    str_start += len_word
                if word_start - str_start == len_substr:
                    res.append(str_start)


